FormAction.Script:
var radListNotifications = thisForm.controlManager.getControlByName('radioButtonListNotifications')
var numberOfNotes = thisForm.controlManager.getControlByName('tableNotes').getStore().getRange().length;

// store box labels for different locale
var alertsBoxLabel = radListNotifications.items.items[0].boxLabel;
var notesBoxLabel = radListNotifications.items.items[1].boxLabel;

//remove count if exists
var index = alertsBoxLabel.lastIndexOf(' (');
if (index > 0){
alertsBoxLabel = alertsBoxLabel.slice(0, index);
}

index = notesBoxLabel.lastIndexOf(' (');
if (index > 0){
notesBoxLabel = notesBoxLabel.slice(0, index);
}

radListNotifications.items.items[0].setBoxLabel(alertsBoxLabel + " (" + thisForm.formVariables.getFormVariableByName('NumberOfAlerts').value +")");
radListNotifications.items.items[1].setBoxLabel(notesBoxLabel +  " (" + numberOfNotes +")");

var lblNotes = thisForm.controlManager.getControlByName('labelNotes');
var numberOfNotes = thisForm.controlManager.getControlByName('tableNotes').getStore().getRange().length;

// store Notes label for different locale
var lblNotesData = lblNotes.getControlData();
//remove count if exists
var index = lblNotesData.lastIndexOf(' (');
if (index > 0){
lblNotesData = lblNotesData.slice(0, index);
}

index = lblNotesData.lastIndexOf(' (');
if (index > 0){
lblNotesData = lblNotesData.slice(0, index);
}

lblNotes.setControlData(lblNotesData +  " (" + numberOfNotes +")");
